// ImageSlideIn.js

import React, { useRef, useEffect } from 'react';
import './ImageSlideIn.css'; // Ensure to style the image slide-in as needed

const ImageSlideIn = React.forwardRef(({ src, alt, credit }, ref) => {
  const imageRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const image = imageRef.current;
      if (image) {
        const rect = image.getBoundingClientRect();
        const isVisible = rect.top < window.innerHeight && rect.bottom >= 0;
        if (isVisible) {
          image.classList.add('slide-in');
        } else {
          image.classList.remove('slide-in');
        }
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="image-slide-in" ref={ref}>
      <img ref={imageRef} src={src} alt={alt} />
      {credit && <p className="photo-credit">{credit}</p>}
    </div>
  );
});

export default ImageSlideIn;
