import React, { useState } from 'react';
import { Container } from 'react-bootstrap';

export default function MailingList() {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setStatus('');

    // Construct the URL and data for the POST request
    const url = 'https://nocturneravers.us14.list-manage.com/subscribe/post?u=96589083eb15db8d74ebcb1a4&id=81b9d3afeb&f_id=0077aee5f0';
    const data = new URLSearchParams();
    data.append('EMAIL', email);

    try {
      const response = await fetch(url, {
        method: 'POST',
        body: data,
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
      });

      if (response.ok) {
        setStatus('Thank you for subscribing!');
      } else {
        setStatus('Oops! Something went wrong.');
      }
    } catch (error) {
      setStatus('Oops! Something went wrong.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div id="Contact" className="tabcontent">
      <Container>
        <div className="row justify-content-center">
          <div id="mc_embed_signup_shell">
            <style type="text/css">
              {`
                #mc_embed_signup {
                  background: #fff;
                  clear: left;
                  font: 14px Helvetica, Arial, sans-serif;
                  width: 600px;
                  padding: 20px;
                  margin: auto;
                }
                .mc-field-group {
                  margin-bottom: 20px;
                }
                .status-message {
                  margin-top: 10px;
                  color: #d9534f; /* Red for error, use #5bc0de for success */
                }
              `}
            </style>
            <div id="mc_embed_signup">
              <form onSubmit={handleSubmit} id="mc-embedded-subscribe-form" name="mc-embedded-subscribe-form" className="validate">
                <div id="mc_embed_signup_scroll">
                  <h2>Subscribe</h2>
                  <div className="indicates-required">
                    <span className="asterisk">*</span> indicates required
                  </div>
                  <div className="mc-field-group">
                    <label htmlFor="mce-EMAIL">
                      Email Address <span className="asterisk">*</span>
                    </label>
                    <input
                      type="email"
                      name="EMAIL"
                      className="required email form-control"
                      id="mce-EMAIL"
                      required
                      placeholder="Enter your email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </div>
                  <div className="optionalParent">
                    <div className="clear foot">
                      <input
                        type="submit"
                        name="subscribe"
                        id="mc-embedded-subscribe"
                        className="button btn btn-primary"
                        value="Subscribe"
                        disabled={loading}
                      />
                    </div>
                  </div>
                  {status && <div className="status-message">{status}</div>}
                </div>
              </form>
            </div>
          </div>
        </div>
      </Container>
    </div>
  );
}
