import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { Navbar, Nav, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navigation.css';

const Navigation = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(sessionStorage.getItem('activeTab') || 'about');

  useEffect(() => {
    sessionStorage.setItem('activeTab', activeTab);
  }, [activeTab]);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  const openTab = (tabName) => {
    setActiveTab(tabName);
    closeMenu();
  };

  return (
    <div className={`overlay-menu ${menuOpen ? 'open' : ''}`}>
      <Navbar expand="lg" className="custom-navbar">
        <Container>
          <Navbar.Brand className="logo-container">
            <img
              src={`${process.env.PUBLIC_URL}/img/sp-logo.svg`}
              height="30"
              className="align-top color-black logo"
              alt="Stackpackers Logo"
            />
          </Navbar.Brand>
          <Navbar.Toggle
            aria-controls="responsive-navbar-nav"
            className="navbar-toggle"
            onClick={toggleMenu}
          />
          <Navbar.Collapse id="responsive-navbar-nav" className={`justify-content-center ${menuOpen ? 'show' : ''}`}>
            <Nav className="align-items-center custom-nav">
              <Nav.Link as={NavLink} to="/" exact onClick={() => openTab('about')}>About</Nav.Link>
              <Nav.Link as={NavLink} to="/portfolio" onClick={() => openTab('portfolio')}>Portfolio</Nav.Link>
              <Nav.Link as={NavLink} to="/resume" onClick={() => openTab('resume')}>Resume</Nav.Link>
              <Nav.Link as={NavLink} to="/skills" onClick={() => openTab('skills')}>Skills</Nav.Link>
              <Nav.Link as={NavLink} to="/contact" onClick={() => openTab('contact')}>Contact</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <div id="about" className="section" style={{ display: activeTab === 'about' ? 'block' : 'none' }}>About Content</div>
      <div id="portfolio" className="section" style={{ display: activeTab === 'portfolio' ? 'block' : 'none' }}>Portfolio Content</div>
      <div id="resume" className="section" style={{ display: activeTab === 'resume' ? 'block' : 'none' }}>Resume Content</div>
      <div id="skills" className="section" style={{ display: activeTab === 'skills' ? 'block' : 'none' }}>Skills Content</div>
      <div id="contact" className="section" style={{ display: activeTab === 'contact' ? 'block' : 'none' }}>Contact Content</div>
    </div>
  );
};

export default Navigation;
