import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { Navbar, Nav, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navigation.css';

const Navigation = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  return (
    <div className={`overlay-menu ${menuOpen ? 'open' : ''}`}>
      <Navbar expand="lg" className="site-navbar">
        <Container>
          <Navbar.Brand className="logo-container">
            <img
              src={`${process.env.PUBLIC_URL}/img/sp-logo.svg`}
              height="30"
              className="align-top color-black logo"
              alt="Stackpackers Logo"
            />
          </Navbar.Brand>
          <Navbar.Toggle
            aria-controls="responsive-navbar-nav"
            className="toggle-button"
            onClick={toggleMenu}
          />
          <Navbar.Collapse id="responsive-navbar-nav" className={`site-navigation ${menuOpen ? 'offcanvas-menu' : ''}`}>
            <Nav className="site-menu">
              <Nav.Link as={NavLink} to="/" exact onClick={closeMenu} className="tablinks">Home</Nav.Link>
              <Nav.Link as={NavLink} to="/about" onClick={closeMenu} className="tablinks">About</Nav.Link>
              <Nav.Link as={NavLink} to="/music" onClick={closeMenu} className="tablinks">Music</Nav.Link>
              <Nav.Link as={NavLink} to="/events" onClick={closeMenu} className="tablinks">Events</Nav.Link>
              <Nav.Link as={NavLink} to="/contact" onClick={closeMenu} className="tablinks">Contact</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </div>
  );
};

export default Navigation;
