import React from 'react';
import { Container } from 'react-bootstrap';
import './Home.css';


export default function Home() {
  const arrowRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const element = document.getElementById('scroll-target');
      if (element && window.scrollY >= element.offsetTop) {
        arrowRef.current.style.display = 'none';
      } else {
        arrowRef.current.style.display = 'block';
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div id="Home" className="tabcontent">
      <Container fluid className="video-container">
          <video autoPlay loop muted>
          <source src="/img/home-vid.MP4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
          <div className="video-logo-container">
            <img src="/img/stackpackers-logo.svg" alt="Logo" />
          </div>
          <div ref={arrowRef} className="scroll-down-arrow">
          <span></span>
          <span></span>
          <span></span>
        </div>
      </Container>
    </div>
  );
}
